//
// Main JavaScript file
// --------------------------------------------------------
var PLTFRM = {
  init: function () {
    (window.innerWidth < 768) ? PLTFRM.Slider.init() : PLTFRM.Clicker.init();
    this.Resizer.init();
    this.Retina.init();
  },

  Clicker: {
    init: function () {
      this.data.swipe = Swipe(PLTFRM.Phones.clickEl, PLTFRM.Slider.options);
      this.addListeners();
      PLTFRM.Slider.setSlideClasses(0);
    },

    data: {
      swipe: null
    },

    addListeners: function () {
      var triggers = document.querySelectorAll('.functionality');
      for(var i = 0; i < triggers.length; i++) {
        triggers[i].setAttribute('data-index', i);
        triggers[i].addEventListener('click', this.handleClick, false);
      }
    },

    handleClick: function (e) {
      var index = e.currentTarget.dataset.index;
      PLTFRM.Clicker.goToPhone(index);
    },

    goToPhone: function(index) {
      this.data.swipe.slide(index, 500);
    }
  },

  Phones: {
    swipeEl: document.querySelector("#slider"),
    clickEl: document.querySelector('#bigPhonePic')
  },

  Slider: {
    init: function () {
       this.data.swipe = Swipe(PLTFRM.Phones.swipeEl, PLTFRM.Slider.options);
       PLTFRM.Slider.setSlideClasses(0);
    },

    data: {
      swipe: null
    },

    options: {
      startSlide: 0,
      speed: 400,
      auto: 7000,
      draggable: false,
      continuous: true,
      disableScroll: true,
      stopPropagation: true,
      callback: function(index) {
        PLTFRM.Slider.setSlideClasses(index);
      },
    },

    setSlideClasses: function(index) {
      var slides = document.querySelectorAll('.functionality');
      var previousIndex = index - 1;
      var nextIndex = index + 1;
      if (previousIndex < 0) previousIndex = slides.length - 1;
      if (nextIndex >= slides.length) nextIndex = 0;

      for (var i = 0; i < slides.length; i++) {
        slides[i].classList.remove('next');
        slides[i].classList.remove('previous');
        slides[i].classList.remove('active');

        if (slides[i].dataset.index == previousIndex) {
          // Using == on purpose here as dataset returns a string, and we need to compare a number
          slides[i].classList.add('previous');
        } else if (slides[i].dataset.index == nextIndex) {
          slides[i].classList.add('next');
        } else if (slides[i].dataset.index == index) {
          slides[i].classList.add('active');
        }
      }
    }
  },

  Resizer: {
    init: function () {
      window.onresize = function () {
        if(window.innerWidth < 768 && PLTFRM.Slider.data.swipe === null) {
          PLTFRM.Clicker.data.swipe.kill();
          PLTFRM.Clicker.data.swipe = null;
          PLTFRM.Slider.init();
        } else if(window.innerWidth >= 768 && PLTFRM.Clicker.data.swipe === null) {
          setTimeout(function() {
            PLTFRM.Slider.data.swipe.kill();
            PLTFRM.Slider.data.swipe = null;
          }, 200);
          PLTFRM.Clicker.init();
        }
      }
    }
  },

  Retina: {
    init: function() {
      if (window.devicePixelRatio > 1 && Utils.detectIE()) {
        var images  = document.querySelectorAll('img');
        var images_l = images.length;
        for (var i = 0; i < images_l; i++) {
          var splitImages = images[i].src.split(".");
          if(splitImages[1] === "jpg" || splitImages[1] === "png") {
            images[i].src = splitImages[0]+"@2x."+splitImages[1];
          }
        }
      }
    }
  },

  Scroller: {
    toSection: function (el, querySelector, duration) {
      var from = el.offsetTop;
      var to = document.querySelector(querySelector).offsetTop;
      var toScroll = (from > to)? from - to : to;
      var totalPossibleHeight = document.body.offsetHeight - window.innerHeight;
      if (toScroll > totalPossibleHeight) toScroll = totalPossibleHeight - 2 ;

      var duration = duration || 400;
      var perTick = toScroll / duration * 10;
      this.stepScroll(perTick, toScroll)
    },

    stepScroll: function (step, total)  {
      setTimeout(function() {
        var actualYScroll = window.scrollY;
        window.scrollTo(0, actualYScroll + step);
        if(window.scrollY <=  total) {
          PLTFRM.Scroller.stepScroll(step, total)
        }
      }, 10);
    }
  }
}

var Utils = {
  detectIE: function() {
    var ua = window.navigator.userAgent;
    var trident = ua.indexOf('Trident/');
    if (trident > 0) {
      // IE 11 => return version number
      var rv = ua.indexOf('rv:');
      return parseInt(ua.substring(rv + 3, ua.indexOf('.', rv)), 10);
    }
    var edge = ua.indexOf('Edge/');
    if (edge > 0) {
      // Edge (IE 12+) => return version number
      return parseInt(ua.substring(edge + 5, ua.indexOf('.', edge)), 10);
    }
    return false;
  }
};

PLTFRM.init()
